import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Header.css";

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="header">
      {/* Logo */}
      <Link to="/" className="logo">
        WilaN <br /> GuitaR
      </Link>

      {/* Menu Icon for mobile */}
      <div
        className={`menu-icon ${menuOpen ? "open" : ""}`}
        onClick={() => setMenuOpen(!menuOpen)}
      >
        <div className="bar"></div>
        <div className="bar"></div>
        <div className="bar"></div>
      </div>

      {/* Navbar */}
      <ul className={`navbar ${menuOpen ? "active" : ""}`}>
        <li>
          <Link to="/" onClick={() => setMenuOpen(false)}>Home</Link>
        </li>
        <li>
          <Link to="/songs" onClick={() => setMenuOpen(false)}>Songs</Link>
        </li>
        <li>
          <Link to="/chords" onClick={() => setMenuOpen(false)}>Chords</Link>
        </li>
        <li>
          <Link to="/about" onClick={() => setMenuOpen(false)}>About</Link>
        </li>
        <li>
          <Link to="/favorite" onClick={() => setMenuOpen(false)}>Favorite</Link>
        </li>
        <li>
          <Link to="/exit" onClick={() => setMenuOpen(false)}>Exit</Link>
        </li>
      </ul>
    </header>
  );
}

export default Header;
