import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import "./Header.css";
import "boxicons/css/boxicons.min.css";

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [clickCount, setClickCount] = useState(0);
  const clickTimeout = useRef(null);

  // Handle profile click (5 taps to open login)
  const handleProfileClick = () => {
    setClickCount((prev) => {
      const newCount = prev + 1;

      if (newCount === 5) {
        alert("Show login modal here ðŸš€"); // Replace with actual login modal
        return 0;
      }

      // reset after 300ms of inactivity
      clearTimeout(clickTimeout.current);
      clickTimeout.current = setTimeout(() => setClickCount(0), 300);

      return newCount;
    });
  };

  // Close menu when scrolling
  useEffect(() => {
    const handleScroll = () => {
      setMenuOpen(false);
    };
    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header className={`header ${menuOpen ? "active" : ""}`}>
      <Link to="/" className="logo">
        WilaN <br /> GuitaR
      </Link>

      {/* Menu icon */}
      <div
        id="menu-icon"
        className={`bx ${menuOpen ? "bx-x" : "bx-menu"}`}
        onClick={() => setMenuOpen(!menuOpen)}
      ></div>

      {/* Navbar */}
      <ul className={`navbar ${menuOpen ? "active" : ""}`}>
        <li>
          <Link to="/songs">songs</Link>
        </li>
        <li>
          <Link to="/chords">chords</Link>
        </li>
        <li>
          <Link to="/about">about</Link>
        </li>
        <li>
          <Link to="/favorite">favorite</Link>
        </li>
        <li>
          <Link to="/exit">exit</Link>
        </li>
        <li>
          <a
            id="loginButton"
            onClick={handleProfileClick}
            style={{ cursor: "pointer" }}
          >
            <i className="bx bxs-user-circle"></i>
          </a>
        </li>
      </ul>
    </header>
  );
}

export default Header;
