import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./CChords.css";

const CChords = () => {
  const [loading, setLoading] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const redirectWithLoading = (url) => {
    setLoading(true);
    setTimeout(() => {
      window.location.href = url;
    }, 300); // 0.3s delay
  };

  return (
    <>
      {/* Header/Navbar */}
      <header>
        <a onClick={() => redirectWithLoading("/")} className="logo">
          WilaN<br />GuitaR
        </a>
        <div
          className={`bx ${menuOpen ? "bx-x" : "bx-menu"}`}
          id="menu-icon"
          onClick={() => setMenuOpen(!menuOpen)}
        ></div>

        <ul className={`navbar ${menuOpen ? "active" : ""}`}>
          <li>
            <Link to="/">home</Link>
          </li>
          <li>
            <Link to="/songs">songs</Link>
          </li>
          <li>
            <a href="#favorite">favorite</a>
          </li>
        </ul>
      </header>

      {/* Loading Screen */}
      {loading && (
        <div id="loading-screen">
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Loading... Please wait</p>
          </div>
        </div>
      )}

      {/* Chords Section */}
      <section className="chords" id="chords">
        <div className="heading">
          <h1>C <br /> chord</h1>
        </div>
        <div className="major">
          <img src="/images/C.jpg" alt="C chord" />
        </div>

        <div className="heading">
          <h1>Cm <br /> chord</h1>
        </div>
        <div className="minor">
          <img src="/images/Cm.jpg" alt="Cm chord" />
        </div>

        {/* Sidebar */}
        <div className="sidebar">
          <h1>Choose Chord Here</h1>
          <Link to="/achords">A</Link>
          <Link to="/bchords">B</Link>
          <Link to="/dchords">D</Link>
          <Link to="/echords">E</Link>
          <Link to="/fchords">F</Link>
          <Link to="/gchords">G</Link>
        </div>
      </section>
    </>
  );
};

export default CChords;
