import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [clickCount, setClickCount] = useState(0);
  const [menuOpen, setMenuOpen] = useState(false);

  // Handle profile icon click (5 clicks to open login)
  const handleProfileClick = () => {
    setClickCount((prev) => {
      const newCount = prev + 1;
      if (newCount === 5) {
        setShowLogin(true);
        return 0;
      }
      return newCount;
    });
  };

  // Reset click count if no activity within 300ms
  useEffect(() => {
    if (clickCount > 0) {
      const timeout = setTimeout(() => setClickCount(0), 300);
      return () => clearTimeout(timeout);
    }
  }, [clickCount]);

  // Handle login form submit
  const handleLogin = (e) => {
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    if (username === "admin" && password === "1234") {
      alert("Login successful ✅");
      setShowLogin(false);
      window.location.href = "/admin"; // React route later
    } else {
      alert("Invalid username or password ❌");
    }
  };

  return (
    <div>
      {/* Header */}
      <header>
        <a href="#home" className="logo">
          WilaN <br /> GuitaR
        </a>
        <div
          className={`bx ${menuOpen ? "bx-x" : "bx-menu"}`}
          id="menu-icon"
          onClick={() => setMenuOpen(!menuOpen)}
        ></div>

        <ul className={`navbar ${menuOpen ? "active" : ""}`}>
          <li>
            <a href="/songs">songs</a>
          </li>
          <li>
            <a href="/chords">chords</a>
          </li>
          <li>
            <a href="#about">about</a>
          </li>
          <li>
            <a href="#favorite">favorite</a>
          </li>
          <li>
            <a href="#exit">exit</a>
          </li>
          <li>
            <a
              id="loginButton"
              onClick={handleProfileClick}
              style={{ cursor: "pointer" }}
            >
              <i className="bx bxs-user-circle"></i>
            </a>
          </li>
        </ul>
      </header>

      {/* Login Form */}
      {showLogin && (
        <div id="loginForm">
          <form onSubmit={handleLogin} className="form-content">
            <h2>Login</h2>
            <input type="text" name="username" placeholder="Username" required />
            <input
              type="password"
              name="password"
              placeholder="Password"
              required
            />
            <button type="submit">Login</button>
            <button type="button" onClick={() => setShowLogin(false)}>
              Close
            </button>
          </form>
        </div>
      )}

      {/* Home Section */}
      <section className="home" id="home">
        <div className="home-text">
          <span>Welcome To</span>
          <h1>
            Wilan Guitar <br /> Lyrics & Chords
          </h1>
          <h2>
            Feel free to Play <br /> your favorite songs
          </h2>
          <button className="btn">Play Now</button>
        </div>
        <div className="home-img">
          <img src="/bg.png" alt="Background" />
        </div>
      </section>

      {/* Footer */}
      <section className="footer" id="footer">
        <div className="social">
          <a href="">
            <i className="bx bxl-facebook"></i>
          </a>
          <a href="">
            <i className="bx bxl-twitter"></i>
          </a>
          <a href="">
            <i className="bx bxl-instagram"></i>
          </a>
          <a href="">
            <i className="bx bxl-youtube"></i>
          </a>
        </div>
        <div className="info">
          <h6>©All Rights Reserved • KentWilanFegarido @2024</h6>
        </div>
      </section>
    </div>
  );
}

export default App;
